mapper.insert:
INSERT INTO member (
    business_name, position_name, manager_name, user_name,
    user_id, user_pwd, email, phone, gender, address,
    enroll_date, image_change_path, image_origin_path, birthday
) VALUES (
             #{businessName}, #{positionName}, #{managerName}, #{userName},
             #{userId}, #{userPwd}, #{email}, #{phone}, #{gender}, #{address},
             #{enrollDate}, #{imageChangePath}, #{imageOriginPath}, #{birthday}
         )

mapper.select:
SELECT COUNT(*) > 0
FROM member
WHERE user_id = #{userId}
