mapper.update:
UPDATE MEMBER M
    SET M.BUSINESS_NO = (
                        SELECT B.BUSINESS_NO
                        FROM BUSINESS B
                        WHERE B.BUSINESS_NAME = #{businessName}
                        ),
    M.POSITION_NO = (
                        SELECT P.POSITION_NO
                        FROM POSITION P
                        WHERE P.POSITION_NAME = #{positionName}
                        ),
    M.MANAGER_NO = (
                        SELECT M2.USER_NO
                        FROM MEMBER M2
                        WHERE M2.USER_NAME = #{managerName}
                        ),
    M.USER_NAME = #{userName},
    M.EMAIL = #{email},
    M.PHONE = #{phone},
    M.ADDRESS = #{address},
    M.IMAGE_CHANGE = #{imageChange},
    M.IMAGE_ORIGIN = #{imageOrigin},
    M.BIRTHDAY = #{birthday}
     WHERE M.USER_NO = #{userNo}
